$(function(){$("#scrollspy a").on("click",function(o){o.preventDefault();var i=this.hash;$("html, body").animate({scrollTop:$(i).offset().top},800,function(){window.location.hash=i})}),$(".grid").masonry({percentPosition:!0,columnWidth:".grid-sizer",itemSelector:".item"}),$(".popup").on("click",function(){event.preventDefault(),$("#dialog").show(),$("#dialog").animate({scrollTop:0});var o=$(this).attr("href");$(o).fadeIn(500),$(".popup-block:visible").not(o).hide()}),$(".btn-close, .btn-back").on("click",function(){$("#dialog, .popup-block").hide()})});