$(function(){$("#scrollspy a").on("click",function(o){o.preventDefault();var i=this.hash;$("html, body").animate({scrollTop:$(i).offset().top},800,function(){window.location.hash=i})}),$(".grid").masonry({columnWidth:".grid-sizer",itemSelector:".item",percentPosition:!0}),$(".popup").on("click",function(){$("#dialog").show()}),$(".btn-close").on("click",function(){$("#dialog").hide()})});